import React, { Component } from 'react';
import Home from './components/Home/Home';
import Video from './components/Video/Video';
import Formularios from './components/Formularios/Formularios';
import About from './components/About/About';
import Services from './components/Services/Services';
import Contact from './components/Contact/Contact';

import "./App.css";


class App  extends Component {
  constructor(){
    super();
    this.state = {
      signInEmail: '',
      signInPassword: '',
      route: 'home' ,
      showMe: 'true'
    }

  }



  onEmailChange = (event) => {

    this.setState({signInEmail: event.target.value})
  }

  onPasswordChange = (event) => {
    console.log(this.state.signInPassword);
    this.setState({signInPassword: event.target.value})
  }

  onSumbitSignIn = (event) => {
    fetch('http://localhost:3001/signin', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        email: this.state.signInEmail,
        password: this.state.signInPassword
      })
    })
      .then(response => response.json())
      this.setState({route: 'video'});
  }

onRouteChange = (route) => {
    this.setState({route: route});
//  this.setState({showMe: !this.state.showMe})
}



render (){

  return (

    <div className='' >

      { this.state.route === 'home'
        ? ( <Home onRouteChange={this.onRouteChange}/> )
        : this.state.route === 'form'
          ? ( <Formularios
            onRouteChange={this.onRouteChange}
            onEmailChange={this.onEmailChange}
            /> )
          : this.state.route === 'nosotros'
            ? ( <About onRouteChange={this.onRouteChange}/> )
            : this.state.route === 'video'
              ? (<Video onRouteChange={this.onRouteChange}/>)
              : this.state.route === 'services'
                ? (<Services onRouteChange={this.onRouteChange}/>)
                : this.state.route === 'contact'
                  ? (<Contact onRouteChange={this.onRouteChange}/>)
                  : null

       }

    </div>


);

}
}


export default App;
