import React, { Component } from 'react';
import Footer from '../Footer/Footer';
import Form from '../Form/Form';
import Sidebar from '../Sidebar/Sidebar';
import Logo from '../Logo/Logo';
import validate from "./validate.js"
import "./Formularios.css";



class Formularios  extends Component {
  constructor(){
    super();
    this.state = {
      route: 'home',
      showMe: 'true',
      formControls: {
        signInEmpresa:{
          value:'',
          valid:'false',
          touched:'false',
          validationRules:{
            minLength: 9
          }
        }
      }
    }
  }


onRouteChange = (route) => {
  this.setState({route: route});
}



onChangeHandler = (event) => {
  const value = event.target.value;
  const signInEmpresa = event.target.name;


  const updatedControls = {
    ...this.state.formControls
  };
  console.dir(updatedControls);

  const updatedFormElement = {
    ...updatedControls[signInEmpresa]
  };
  console.dir(updatedControls);

  updatedFormElement.value  = value;
  updatedFormElement.touched = true;
  updatedFormElement.valid = validate(value, updatedFormElement.validationRules);

  updatedControls[signInEmpresa] = updatedFormElement;

  //console.dir(updatedControls[signInEmpresa]);

  this.setState({
    formControls: updatedControls
  });
}

componentDidMount() {
    window.scrollTo(0, 0);
}

onSumbitSignIn = (event) => {

  const errorEmpresa =this.errorEmpresa;

  if(errorEmpresa)
  {
    fetch('http://localhost:3001/form', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({

        empresa: this.state.formControls.ignInEmpresa,


      })
    })
        .then(response => response.json())
        .then(response => {
          if (response === 'success') {
            this.props.onRouteChange('video');
          } else {
            this.props.onRouteChange('contact');
          }
        })

  } else {
    alert('Error');
  }


}

render (){

  return (

      <div className=''>
            <Sidebar
              onRouteChange={this.props.onRouteChange}
              pageWrapId={"page-wrap"}
              outerContainerId={"App"}
              width={ '50%' }
              right
                />
            <Logo className='fixed'/>
            <Form
            onSumbitSignIn={this.onSumbitSignIn}
            onChangeHandler={this.onChangeHandler}
            />
            <Footer />
      </div>

  );

  }

}


export default Formularios;
