import React from 'react';
import './Form.css';

const Form  = ( {
                  onChangeHandler,
                  onSumbitSignIn,

                  isnombreTouched,
                  isnombreValid,
                  isapellidoValid,
                  isapellidoTouched,
                  isEmailValid,
                  isEmailTouched,
                  isTelefonoValid,
                  isTelefonoTouched,
                  isEmpresaTouched,
                  isEmpresaValid


                } ) => {


  let formControl = "form-control";

  if ( isnombreTouched === true ){
    formControl = 'form-control form-error';
  } else if (isnombreValid === true) {
    formControl = 'form-control';
  }


  return (

  <div className=" tc blue pv5" >

    <section>
    <div className="tc center ">
       <h1 className="lh-solid f2 ttu toppos">trabajemos JUNTOS</h1>
    </div>
      <div className="pt4 pb2 center">
          <h1 className="f-5-l f1-m f2-ns lh-solid">Cuentanos tu Proyecto</h1>
          <p className="f3 w-50 center ">
            Tus ideas, objetivos la podemos materializar en algo concreto. Te enviaremos una propuesta sin compromiso.
          </p>
     </div>
     <div className=" center measure ">
         <h1 className="f2">Listo para empezar?</h1>
    </div>

      <div className="">

          <form className=" flex flex-column center measure ">

            <div className="flex justify-center mv2 center">

              <div className=" ">
                <label></label>
                  <input
                       onChange = {onChangeHandler}
                       className= {formControl}
                       type="text"
                       name="nombre"
                       id="nombre"
                       placeholder="Nombre"
                       required
                       value= {onChangeHandler.nombre}
                       touched={isnombreTouched}
                       valid={isnombreValid}
                  />


              </div>

              <div>
                <label></label>
                  <input
                       onChange = {onChangeHandler}
                       className= {formControl}
                       type="text"
                       name="apellido"
                       id="apellido"
                       placeholder="Apellido"
                       required
                       value= {onChangeHandler.apellido}
                       touched={isapellidoTouched}
                       valid={isapellidoValid}
                  />

              </div>

            </div>

            <div className="mv2">
              <label></label>

                <input
                     onChange = {onChangeHandler}
                     className= {formControl}
                     type="email"
                     name="email"
                     id="email"
                     placeholder="Email@mail.com"
                     required
                     value= {onChangeHandler.email}
                     touched={isEmailTouched}
                     valid={isEmailValid}
                />

            </div>

            <div className="mv2">
              <label></label>

                <input
                     onChange = {onChangeHandler}
                     className= {formControl}
                     type="tel"
                     name="telefono"
                     id="telefono"
                     placeholder="+51+(Number)"
                     required
                     value= {onChangeHandler.telefono}
                     touched={isTelefonoTouched}
                     valid={isTelefonoValid}
                 />

            </div>

            <div className="mv2 ">
              <label></label>

                <input
                     onChange = {onChangeHandler}
                     className= {formControl}
                     type="text"
                     name="empresa"
                     id="empresa"
                     placeholder="Empresa"
                     required
                     value= {onChangeHandler.empresa}
                     touched={isEmpresaTouched}
                     valid={isEmpresaValid}

                />

            </div>


            <div className="mv3">
              <label></label>
                <input
                  className="f3-l pointer f5 tracked-tight dib  pv3 ph4-l ph3 br2 ba color-boton grow "
                  type="submit"
                  value="ENVIAR"
                  onClick={onSumbitSignIn}
                />

            </div>



          </form>

      </div>

    </section>
  </div>




  );


}

export default Form;
