import React, { Component } from 'react';
import Footer from '../Footer/Footer';
import Form from '../Form/Form';
import Sidebar from '../Sidebar/Sidebar';
import Logo from '../Logo/Logo';

import "./Formularios.css";

class Formularios  extends Component {
  constructor(){
    super();
    this.state = {
      route: 'home',
      showMe: 'true',
      signInNombre: '',
      signInApellido: '',
      signInEmail: '',
      signInTelefono: '',
      signInEmpresa: '',
      signInPresupuesto: '',
      signInDeadline: '',
      signInMensaje: '',
      signInPolitica: ''
    }
  }

onRouteChange = (route) => {
  this.setState({route: route});
}

onNombreChange = (event) => {
  this.setState({signInNombre: event.target.value})
}

onApellidoChange = (event) => {
  this.setState({signInApellido: event.target.value})
}

onEmailChange = (event) => {
  this.setState({signInEmail: event.target.value})
}

onTelefonoChange = (event) => {
  this.setState({signInTelefono: event.target.value})
}

onEmpresaChange = (event) => {
  if(event.target.value != null){
    this.setState({signInEmpresa: event.target.value})
  }else {
    alert("Error: error message");
  }
}

onPresupuestoChange = (event) => {
  this.setState({signInPresupuesto: event.target.value})
}

onDeadlineChange = (event) => {
  this.setState({signInDeadline: event.target.value})
}

onMensajeChange = (event) => {
  this.setState({signInMensaje: event.target.value})
}

onPoliticaChange = (event) => {
  this.setState({signInPolitica: event.target.value})
}


componentDidMount() {
    window.scrollTo(0, 0);
//    this.refs.form.onsubmit = () => this.login();
}




onSumbitSignIn = (event) => {
  fetch('http://localhost:3001/form', {
    method: 'post',
    headers: {'Content-Type': 'application/json'},
    body: JSON.stringify({
      nombre: this.state.signInNombre,
      apellido: this.state.signInApellido,
      email: this.state.signInEmail,
      telefono: this.state.signInTelefono,
      empresa: this.state.signInTelefono,
      presupuesto: this.state.signInPresupuesto,
      deadline: this.state.signInDeadline,
      mensaje: this.state.signInMensaje,
      politica: this.state.signInPolitica

    })
  })
      .then(response => response.json())
      .then(response => {
        if (response === 'success') {
          this.props.onRouteChange('video');
        } else {
          this.props.onRouteChange('contact');
        }
      })

}

render (){

  return (

      <div className=''>
            <Sidebar
              onRouteChange={this.props.onRouteChange}
              pageWrapId={"page-wrap"}
              outerContainerId={"App"}
              width={ '50%' }
              right
                />
            <Logo className='fixed'/>
            <Form
            onNombreChange={this.onNombreChange}
            onApellidoChange={this.onApellidoChange}
            onEmailChange={this.onEmailChange}
            onPasswordChange={this.onPasswordChange}
            onTelefonoChange={this.onTelefonoChange}
            onEmpresaChange={this.onEmpresaChange}
            onPresupuestoChange={this.onPresupuestoChange}
            onDeadlineChange={this.onDeadlineChange}
            onMensajeChange={this.onMensajeChange}
            onPoliticaChange={this.onPoliticaChange}
            onSumbitSignIn={this.onSumbitSignIn}
            />
            <Footer />
      </div>

  );

  }

}


export default Formularios;
