import React, { Component } from 'react';
import Cabecera from '../Cabecera/Cabecera';
import Footer from '../Footer/Footer';
import Contenido from '../Contenido/Contenido';
import Particles from 'react-particles-js';
import Sidebar from '../Sidebar/Sidebar';
import Logo from '../Logo/Logo';
import Formularios from './components/Formularios/Formularios';


import "./Home.css";


const particlesOptions = {
  "particles": {
      "number": {
          "value": 100,
          "density.value_area": 100
      },
      "size": {
          "value": 5
      }
  },
  "interactivity": {
       "detect_on": "window",
      "events": {
          "onhover": {
              "enable": true,
              "mode": "repulse"
          }
      }
  }
}

class Home  extends Component {
  constructor(props){
    super(props);
    this.state = {
      route: 'home',
      showMe: 'true'
    }
  }

  onRouteChange = (route) => {
  this.setState({route: route});

}

render (){

  return (

      <div className=''>
            <Sidebar
              onRouteChange={this.props.onRouteChange}
              />
              { this.state.route === 'form'
             ? <Formularios onRouteChange={this.onRouteChange}/>
             : null
              }
            <Particles className="particles"
                params={particlesOptions} />
            <Logo />
            <Cabecera onRouteChange={this.props.onRouteChange}/>
            <Contenido onRouteChange={this.props.onRouteChange}/>
            <Footer onRouteChange={this.props.onRouteChange}/>
      </div>

  );

  }

}


export default Home;
