import React, { Component } from 'react';
import Footer from '../Footer/Footer';
import Form from '../Form/Form';
import Sidebar from '../Sidebar/Sidebar';
import Logo from '../Logo/Logo';
import validate from "./validate.js"
import "./Formularios.css";



class Formularios  extends Component {
  constructor(){
    super();
    this.state = {
      route: 'home',
      showMe: 'true',
      formControls: {
        nombre:{
          value:'',
          valid:'false',
          touched:'false',
          validationRules:{
            minLength: 3
          }
        },
        apellido:{
          value:'',
          valid:'false',
          touched:'false',
          validationRules:{
            minLength: 3
          }
        },
        email:{
          value:'',
          valid:'false',
          touched:'false',
          validationRules:{
            minLength: 3
          }
        },
        telefono:{
          value:'',
          valid:'false',
          touched:'false',
          validationRules:{
            minLength: 3
          }
        },
        empresa:{
          value:'',
          valid:'false',
          touched:'false',
          validationRules:{
            minLength: 3
          }
        },

      }
    }
  }


onRouteChange = (route) => {
  this.setState({route: route});
}



onChangeHandler = (event) => {

  console.dir(this.state.formControls);

  const value = event.target.value;
  const name = event.target.name;

  console.dir(this.state.formControls);

  const updatedControls = {
    ...this.state.formControls
  };
  console.dir(updatedControls);

  const updatedFormElement = {
    ...updatedControls[name]
  };
  console.dir(updatedFormElement);

  updatedFormElement.value  = value;
  updatedFormElement.touched = true;
  updatedFormElement.valid = validate(value, updatedFormElement.validationRules);

  console.dir(updatedFormElement);

  updatedControls[name] = updatedFormElement;

  console.dir(updatedControls);

  this.setState({
    formControls: updatedControls
  });
}

componentDidMount() {
    window.scrollTo(0, 0);
}

onSumbitSignIn = (event) => {

  const errorEmpresa =this.state.formControls.empresa.valid;
  console.log(errorEmpresa);
console.log(this.state.formControls.empresa.value);
  if(errorEmpresa)
  {
    fetch('http://localhost:3001/form', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        empresa: this.state.formControls.empresa.value,
      })
    })
        .then(response => response.json())
        .then(response => {
          if (response === 'success') {
            this.props.onRouteChange('video');
          } else {
            this.props.onRouteChange('contact');
          }
        })

  } else {
    alert('Error');
  }


}

render (){

  return (

      <div className=''>
            <Sidebar
              onRouteChange={this.props.onRouteChange}
              pageWrapId={"page-wrap"}
              outerContainerId={"App"}
              width={ '50%' }
              right
                />
            <Logo className='fixed'/>
            <Form
            isnombreValid={this.state.formControls.nombre.valid}
            isnombreTouched={this.state.formControls.nombre.touched}
            isapellidoValid={this.state.formControls.apellido.valid}
            isapellidoTouched={this.state.formControls.apellido.touched}
            isEmailValid={this.state.formControls.email.valid}
            isEmailTouched={this.state.formControls.email.touched}
            isTelefonoValid={this.state.formControls.telefono.valid}
            isTelefonoTouched={this.state.formControls.telefono.touched}
            isEmpresaValid={this.state.formControls.empresa.valid}
            isEmpresaTouched={this.state.formControls.empresa.touched}

            onSumbitSignIn={this.onSumbitSignIn}
            onChangeHandler={this.onChangeHandler}
            />
            <Footer />
      </div>

  );

  }

}


export default Formularios;
