import React, { Component } from 'react';
import Home from './components/Home/Home';
import Video from './components/Video/Video';
import Formularios from './components/Formularios/Formularios';
import About from './components/About/About';

import "./App.css";


class App  extends Component {
  constructor(){
    super();
    this.state = {
      signInEmail: '',
      signInPassword: '',
      route: 'home' ,
      showMe: 'true'
    }
  }




  onEmailChange = (event) => {
    console.log(this.state.signInEmail);
    this.setState({signInEmail: event.target.value})
  }

  onPasswordChange = (event) => {
    this.setState({signInPassword: event.target.value})
  }

  onSumbitSignIn = (event) => {
    fetch('http://localhost:3001/signin', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        email: this.state.signInEmail,
        password: this.state.signInPassword
      })
    })
      .then(response => response.json())
  }

onRouteChange = (route) => {
  this.setState({route: route});
  this.setState({showMe: !this.state.showMe})


}

render (){

  return (

    <div className='App'>


          { this.state.showMe &&  <Home onRouteChange={this.onRouteChange}/>
          }
          { this.state.route === 'form'
             ? <Formularios
               onEmailChange={this.onEmailChange}
               onRouteChange={this.onRouteChange}/>
             : null
           }
           { this.state.route === 'video'
              ? <Video onRouteChange={this.onRouteChange}/>
              : null
            }
            { this.state.route === 'nosotros'
               ? <About onRouteChange={this.onRouteChange}/>
               : null
             }
    </div>


);

}
}


export default App;
