import React, { Component } from 'react';
import Cabecera from '../Cabecera/Cabecera';
import Footer from '../Footer/Footer';
import Contenido from '../Contenido/Contenido';
import Particles from 'react-particles-js';
import Sidebar from '../Sidebar/Sidebar';
import Logo from '../Logo/Logo';

import "./Home.css";


const particlesOptions = {
  "particles": {
      "number": {
          "value": 100,
          "density.value_area": 100
      },
      "size": {
          "value": 2
      }
  },
  "interactivity": {
       "detect_on": "window",
      "events": {
          "onhover": {
              "enable": true,
              "mode": "repulse"
          }
      }
  }
}

class Home  extends Component {
  constructor(props){
    super(props);
    this.state = {
      route: ' ',
      subsInEmail: '',
      subsInEmailValid: 'false'
    }
  }

  onRouteChange = (route) => {
      this.setState({route: route});
  //  this.setState({showMe: !this.state.showMe})
  }

  componentDidMount() {
      window.scrollTo(0, 0);
  }


  onSubsChange = (event) => {
    this.setState({subsInEmail: event.target.value, subsInEmailValid:true})
  }

  onSumbitSubscription = (event) => {
    if(this.state.subsInEmailValid === 'true'){

      fetch('http://localhost:3001/subs', {
        method: 'post',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
         email: this.state.subsInEmail,
        })
      })
          .then(response => {
            if (response === 'success') {
              this.props.onRouteChange('video');
            } else {
              this.props.onRouteChange('contact');
            }
          })
    }

  }


render (){

  return (

      <div className=''>
            <Sidebar
              onRouteChange={this.props.onRouteChange} />
            <Particles className="particles"
                params={particlesOptions} />
            <Logo />
            <Cabecera onRouteChange={this.props.onRouteChange}/>
            <Contenido onRouteChange={this.props.onRouteChange}/>
            <Footer
              onRouteChange={this.props.onRouteChange}
              onSubsChange={this.onSubsChange}
              onSumbitSubscription={this.onSumbitSubscription}
            />
        </div>

  );

  }

}


export default Home;
