import React, { Component } from 'react';
import Footer from '../Footer/Footer';
import Form from '../Form/Form';
import Sidebar from '../Sidebar/Sidebar';
import Logo from '../Logo/Logo';

import "./Formularios.css";


const validate = (value, rules) => {
  let isValid = true;

    for(let rule in rules){
      switch (rule) {
        case "minLength":
            isValid = isValid && minLengthValitaor(value, rules[rule]);
        break;

      default: isValid =true;
      }
    }

return isValid;

}

const minLengthValitaor = (value, minLength) => {
  return value.length >= minLength;
}


class Formularios  extends Component {
  constructor(){
    super();
    this.state = {
      route: 'home',
      showMe: 'true',
      formControls: {
        signInEmpresa:{
          value:'',
          valid:'false',
          touched:'false',
          validationRules:{
            minLength:3
          }
        }
      }
    }
  }


onRouteChange = (route) => {
  this.setState({route: route});
}



onChangeHandler = (event) => {
  const value = event.target.value;
  const signInEmpresa = event.target.name;

  const updatedControls = {
    ...this.state.formControls
  };

  const updatedFormElement = {
    ...updatedControls[signInEmpresa]
  };


  updatedFormElement.value  = value;
  updatedFormElement.touched = true;
  updatedFormElement.valid = validate(value, updatedFormElement.validationRules);

  updatedControls[signInEmpresa] = updatedFormElement;

  this.setState({
    formControls: updatedControls
  });

  console.log(this.errorEmpresa);
  console.log(this.touchEmpresa);

  this.setState({signInEmpresa: event.target.value});

}



componentDidMount() {
    window.scrollTo(0, 0);
}




onSumbitSignIn = (event) => {

  const errorEmpresa =this.errorEmpresa;

  if(errorEmpresa)
  {
    fetch('http://localhost:3001/form', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({

        empresa: this.state.signInEmpresa,


      })
    })
        .then(response => response.json())
        .then(response => {
          if (response === 'success') {
            this.props.onRouteChange('video');
          } else {
            this.props.onRouteChange('contact');
          }
        })

  } else {
    alert('Error');
  }


}

render (){

  return (

      <div className=''>
            <Sidebar
              onRouteChange={this.props.onRouteChange}
              pageWrapId={"page-wrap"}
              outerContainerId={"App"}
              width={ '50%' }
              right
                />
            <Logo className='fixed'/>
            <Form
            onNombreChange={this.onNombreChange}
            onApellidoChange={this.onApellidoChange}
            onEmailChange={this.onEmailChange}
            onPasswordChange={this.onPasswordChange}
            onTelefonoChange={this.onTelefonoChange}
            onEmpresaChange={this.onEmpresaChange}
            onPresupuestoChange={this.onPresupuestoChange}
            onDeadlineChange={this.onDeadlineChange}
            onMensajeChange={this.onMensajeChange}
            onPoliticaChange={this.onPoliticaChange}
            onSumbitSignIn={this.onSumbitSignIn}
            onChangeHandler={this.onChangeHandler}
            />
            <Footer />
      </div>

  );

  }

}


export default Formularios;
